import React from 'react';
import { Link } from 'react-router-dom';

function Navbar() {
  const style = {
    backgroundColor: '#000',       // classic black
    color: '#fff',
    padding: '15px 40px',
    display: 'flex',
    justifyContent: 'center',      // center the nav items for symmetry
    gap: '40px',                   // more spacing for a luxe feel
    fontFamily: "'Didot', 'Times New Roman', serif",  // Didot is iconic for luxury fashion
    fontWeight: '500',
    fontSize: '1.1rem',
    letterSpacing: '0.12em',       // add subtle letter spacing for elegance
    position: 'sticky',
    top: 0,
    zIndex: 1000,
    borderBottom: '1px solid #d4af37'  // thin gold underline for subtle luxury
  };

  const linkStyle = {
    color: '#d4af37',             // gold
    textDecoration: 'none',
    transition: 'color 0.3s ease',
  };

  const linkHoverStyle = {
    color: '#fff',                // on hover, switch to white for subtle contrast
  };

  return (
    <nav style={style}>
      {['Home', 'New Product', 'QR Generate', 'QR Scan', 'Dashboard', 'Map'].map((text, idx) => (
        <Link
          key={idx}
          to={`/${text.toLowerCase().replace(' ', '-')}`}
          style={linkStyle}
          onMouseEnter={e => (e.target.style.color = linkHoverStyle.color)}
          onMouseLeave={e => (e.target.style.color = linkStyle.color)}
        >
          {text}
        </Link>
      ))}
    </nav>
  );
}

export default Navbar;
