import React from 'react';
import { useSearchParams } from 'react-router-dom';
import QRCode from 'qrcode';

function QRGenerate() {
  const [searchParams] = useSearchParams();
  const link = searchParams.get('link') || '';
  const [qrUrl, setQrUrl] = React.useState('');

  React.useEffect(() => {
    if (link) {
      QRCode.toDataURL(link, { width: 200, margin: 2 })
        .then(url => setQrUrl(url))
        .catch(() => setQrUrl(''));
    } else {
      setQrUrl('');
    }
  }, [link]);

  const containerStyle = {
    textAlign: 'center',
    marginTop: '40px',
    fontFamily: '"Times New Roman", serif',
    color: '#000',
  };

  const linkStyle = {
    color: '#d4af37',
    fontWeight: 'bold',
    textDecoration: 'none',
    wordBreak: 'break-word',
    marginTop: '15px',
    display: 'inline-block',
  };

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h2', null, 'QR Code for your product'),
    link
      ? qrUrl
        ? React.createElement('img', { src: qrUrl, alt: 'QR Code' })
        : React.createElement('p', null, 'Generating QR code...')
      : React.createElement('p', null, 'No link provided to generate QR code.'),
    link &&
      React.createElement(
        'a',
        { href: link, target: '_blank', rel: 'noreferrer', style: linkStyle },
        link
      )
  );
}

export default QRGenerate;
