import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function Map() {
  const containerStyle = {
    fontFamily: "'Playfair Display', serif",
    color: '#1a1a1a',
    backgroundColor: '#fff',
    height: '80vh',
    padding: '40px 20px',
    display: 'flex',
    flexDirection: 'column',
  };

  const headingStyle = {
    fontSize: '3rem',
    fontWeight: 300,
    marginBottom: '24px',
    textTransform: 'uppercase',
    letterSpacing: '0.1em',
    textAlign: 'center',
  };

  const mapWrapperStyle = {
    display: 'flex',
    flex: 1,
    gap: '20px',
    maxWidth: '900px',
    margin: '0 auto',
  };

  const mapStyle = {
    flex: 2,
    height: '60vh',
    borderRadius: '8px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    border: '1px solid #ddd',
  };

  const listStyle = {
    flex: 1,
    overflowY: 'auto',
    maxHeight: '60vh',
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '10px',
    fontSize: '1rem',
    backgroundColor: '#fafafa',
  };

  const inputStyle = {
    padding: '8px 12px',
    marginBottom: '12px',
    fontSize: '1rem',
    borderRadius: '4px',
    border: '1px solid #ccc',
    width: '100%',
    boxSizing: 'border-box',
  };

  const stores = [
    { id: 1, name: 'Paris Store', coords: [48.866667, 2.333333], address: '15 Rue de la Paix, Paris' },
    { id: 2, name: 'Marseille Store', coords: [43.296482, 5.36978], address: '12 Quai des Belges, Marseille' },
    { id: 3, name: 'Lyon Store', coords: [45.764043, 4.835659], address: '20 Rue de la République, Lyon' },
    { id: 4, name: 'Lille Store', coords: [50.62925, 3.057256], address: '33 Rue Faidherbe, Lille' },
    { id: 5, name: 'Toulouse Store', coords: [43.604652, 1.444209], address: '11 Place du Capitole, Toulouse' },
    { id: 6, name: 'Bordeaux Store', coords: [44.837789, -0.57918], address: '5 Cours de l’Intendance, Bordeaux' },
    { id: 7, name: 'Reims Store', coords: [49.258329, 4.031696], address: '7 Place Drouet d’Erlon, Reims' },
    { id: 8, name: 'Nantes Store', coords: [47.218371, -1.553621], address: '25 Rue Crébillon, Nantes' },
    { id: 9, name: 'Grenoble Store', coords: [45.188529, 5.724524], address: '10 Rue Lafayette, Grenoble' },
    { id: 10, name: 'Nice Store', coords: [43.710173, 7.261953], address: '18 Avenue Jean Médecin, Nice' },
  ];

  const mapRef = useRef(null);
  const markersRef = useRef({});
  const [search, setSearch] = useState('');

  useEffect(() => {
    mapRef.current = L.map('map').setView([48.8566, 2.3522], 5);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution:
        '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(mapRef.current);

    const redIcon = L.icon({
      iconUrl: 'https://cdn.jsdelivr.net/npm/leaflet@1.9.4/dist/images/marker-icon-2x-red.png',
      shadowUrl: 'https://cdn.jsdelivr.net/npm/leaflet@1.9.4/dist/images/marker-shadow.png',
      iconSize: [25, 41],
      iconAnchor: [12, 41],
      popupAnchor: [1, -34],
      shadowSize: [41, 41],
    });

    stores.forEach(({ id, coords, address, name }) => {
      const marker = L.marker(coords, { icon: redIcon })
        .addTo(mapRef.current)
        .bindPopup(`<b>${name}</b><br>${address}`);
      markersRef.current[id] = marker;
    });

    return () => {
      mapRef.current.remove();
      markersRef.current = {};
    };
  }, []);

  // Filtered stores based on search input
  const filteredStores = stores.filter(
    (store) =>
      store.name.toLowerCase().includes(search.toLowerCase()) ||
      store.address.toLowerCase().includes(search.toLowerCase())
  );

  // Handler for clicking on store in list
  function handleStoreClick(id) {
    const marker = markersRef.current[id];
    if (marker) {
      mapRef.current.setView(marker.getLatLng(), 13, { animate: true });
      marker.openPopup();
    }
  }

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h2', { style: headingStyle }, 'Our Boutiques'),

    React.createElement('input', {
      type: 'search',
      placeholder: 'Search boutiques by name or address...',
      value: search,
      onChange: (e) => setSearch(e.target.value),
      style: inputStyle,
    }),

    React.createElement(
      'div',
      { style: mapWrapperStyle },

      React.createElement('div', { id: 'map', style: mapStyle }),

      React.createElement(
        'div',
        { style: listStyle },
        filteredStores.length === 0
          ? 'No boutiques found.'
          : filteredStores.map((store) =>
              React.createElement(
                'div',
                {
                  key: store.id,
                  onClick: () => handleStoreClick(store.id),
                  style: {
                    padding: '8px',
                    marginBottom: '8px',
                    cursor: 'pointer',
                    borderRadius: '4px',
                    border: '1px solid transparent',
                    transition: 'all 0.2s',
                    userSelect: 'none',
                  },
                  onMouseEnter: (e) =>
                    (e.currentTarget.style.border = '1px solid #000'),
                  onMouseLeave: (e) =>
                    (e.currentTarget.style.border = '1px solid transparent'),
                },
                React.createElement('b', null, store.name),
                React.createElement('div', null, store.address)
              )
            )
      )
    )
  );
}

export default Map;
