import React from 'react';

function Dashboard() {
  const [products, setProducts] = React.useState({});

  React.useEffect(() => {
    const prods = JSON.parse(localStorage.getItem('products') || '{}');
    setProducts(prods);
  }, []);

  const containerStyle = {
    maxWidth: '600px',
    margin: '40px auto',
    fontFamily: '"Times New Roman", serif',
    color: '#000'
  };

  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse'
  };

  const thStyle = {
    borderBottom: '2px solid #d4af37',
    padding: '10px',
    textAlign: 'left'
  };

  const tdStyle = {
    borderBottom: '1px solid #ddd',
    padding: '10px'
  };

  return React.createElement('div', {style: containerStyle},
    React.createElement('h2', null, 'Products Dashboard'),
    Object.keys(products).length === 0
      ? React.createElement('p', null, 'No products created yet.')
      : React.createElement('table', {style: tableStyle},
        React.createElement('thead', null,
          React.createElement('tr', null,
            React.createElement('th', {style: thStyle}, 'Name'),
            React.createElement('th', {style: thStyle}, 'Size'),
            React.createElement('th', {style: thStyle}, 'Model')
          )
        ),
        React.createElement('tbody', null,
          Object.entries(products).map(([id, product]) =>
            React.createElement('tr', {key: id},
              React.createElement('td', {style: tdStyle}, product.name),
              React.createElement('td', {style: tdStyle}, product.size),
              React.createElement('td', {style: tdStyle}, product.model)
            )
          )
        )
      )
  );
}

export default Dashboard;
