const React = require('react');

const img1 = require('../images/img1.jpg');
const img2 = require('../images/img2.jpg');
const img3 = require('../images/img3.jpg');
const img4 = require('../images/img4.jpg');
const img5 = require('../images/img5.jpg');
const img6 = require('../images/img6.jpg');

function Home() {
  const containerStyle = {
    fontFamily: "'Playfair Display', serif",
    color: '#1a1a1a',
    backgroundColor: '#fff',
    minHeight: '80vh',
    padding: '80px 20px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  };

  const headingStyle = {
    fontSize: '5rem',
    fontWeight: 300,
    letterSpacing: '0.15em',
    margin: 0,
    textTransform: 'uppercase',
    lineHeight: 1.1,
  };

  const lineStyle = {
    width: '60px',
    height: '2px',
    backgroundColor: '#000',
    margin: '32px 0',
    opacity: 0.3,
  };

  const pStyle = {
    fontSize: '1.25rem',
    maxWidth: '460px',
    fontWeight: 300,
    letterSpacing: '0.05em',
    lineHeight: 1.6,
    color: '#444',
    margin: 0,
  };

  const galleryStyle = {
    marginTop: '50px',
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '30px',
    width: '100%',
    maxWidth: '1000px',
  };

  const imgStyle = {
    width: '100%',
    height: '350px',
    objectFit: 'cover',
    borderRadius: '10px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    cursor: 'pointer',
  };

  const images = [img1, img2, img3, img4, img5, img6];

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h1', { style: headingStyle }, 'DIOR'),
    React.createElement('div', { style: lineStyle }),
    React.createElement('p', { style: pStyle }, 'Experience the elegance of Dior’s iconic products and discover our exclusive boutiques worldwide.'),
    React.createElement(
      'div',
      { style: galleryStyle },
      images.map(function(src, idx) {
        return React.createElement('img', {
          key: idx,
          src: src,
          alt: 'Dior clothing ' + (idx + 1),
          style: imgStyle,
          onMouseEnter: function(e) {
            e.currentTarget.style.transform = 'scale(1.05)';
            e.currentTarget.style.boxShadow = '0 10px 20px rgba(0,0,0,0.2)';
          },
          onMouseLeave: function(e) {
            e.currentTarget.style.transform = 'scale(1)';
            e.currentTarget.style.boxShadow = '0 4px 12px rgba(0,0,0,0.1)';
          },
        });
      })
    )
  );
}

module.exports = Home;
