import React from 'react';

function Home() {
  const containerStyle = {
    fontFamily: "'Playfair Display', serif",
    color: '#1a1a1a',
    backgroundColor: '#fff',
    minHeight: '80vh',
    padding: '80px 20px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  };

  const headingStyle = {
    fontSize: '5rem',
    fontWeight: 300, // light weight
    letterSpacing: '0.15em',
    margin: 0,
    textTransform: 'uppercase',
    lineHeight: 1.1,
  };

  const lineStyle = {
    width: '60px',
    height: '2px',
    backgroundColor: '#000',
    margin: '32px 0',
    opacity: 0.3,
  };

  const pStyle = {
    fontSize: '1.25rem',
    maxWidth: '460px',
    fontWeight: 300,
    letterSpacing: '0.05em',
    lineHeight: 1.6,
    color: '#444',
    margin: 0,
  };

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h1', { style: headingStyle }, 'DIOR'),
    React.createElement('div', { style: lineStyle }),
    React.createElement(
      'p',
      { style: pStyle },
      'Experience the elegance of Dior’s iconic products and discover our exclusive boutiques worldwide.'
    )
  );
}

export default Home;
