import React from 'react';

function ScanQR() {
  const containerStyle = {
    fontFamily: "'Playfair Display', serif",
    color: '#1a1a1a',
    backgroundColor: '#fff',
    minHeight: '80vh',
    padding: '80px 20px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  };

  const headingStyle = {
    fontSize: '3rem',
    fontWeight: 300,
    letterSpacing: '0.1em',
    marginBottom: '40px',
    textTransform: 'uppercase',
  };

  const pStyle = {
    fontSize: '1.25rem',
    maxWidth: '400px',
    fontWeight: 300,
    letterSpacing: '0.05em',
    color: '#555',
  };

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h2', { style: headingStyle }, 'Scan QR Code'),
    React.createElement(
      'p',
      { style: pStyle },
      'QR scanning functionality coming soon. Please use the generated QR codes to explore Dior products.'
    )
  );
}

export default ScanQR;
