import React from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function MapPage() {
  const mapRef = React.useRef(null);
  const [map, setMap] = React.useState(null);

  // Dior-like gold and black color scheme for markers
  const goldIcon = new L.Icon({
    iconUrl:
      'https://cdn-icons-png.flaticon.com/512/190/190411.png', // simple gold pin icon
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowUrl:
      'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.4/images/marker-shadow.png',
    shadowSize: [41, 41]
  });

  const locations = [
    {id: 1, name: 'Dior Store Paris', lat: 48.8655, lng: 2.3212, address: '33 Avenue Montaigne, 75008 Paris'},
    {id: 2, name: 'Dior Store New York', lat: 40.7636, lng: -73.9746, address: '745 5th Ave, New York, NY 10151'},
    {id: 3, name: 'Dior Store London', lat: 51.5079, lng: -0.1447, address: '164-168 Sloane St, London SW1X 9BW'},
    {id: 4, name: 'Dior Store Tokyo', lat: 35.6718, lng: 139.7644, address: '3 Chome-6-1 Ginza, Chuo City, Tokyo 104-0061'},
    {id: 5, name: 'Dior Store Milan', lat: 45.4668, lng: 9.1905, address: 'Via Montenapoleone, 20121 Milan'},
    {id: 6, name: 'Dior Store Dubai', lat: 25.2048, lng: 55.2708, address: 'The Dubai Mall, Dubai'},
    {id: 7, name: 'Dior Store Los Angeles', lat: 34.0736, lng: -118.4004, address: '9670 Wilshire Blvd, Beverly Hills, CA'},
    {id: 8, name: 'Dior Store Hong Kong', lat: 22.2794, lng: 114.1628, address: '188 Queens Rd, Central, Hong Kong'},
    {id: 9, name: 'Dior Store Sydney', lat: -33.8688, lng: 151.2093, address: 'Westfield Sydney, Sydney NSW'},
    {id: 10, name: 'Dior Store Seoul', lat: 37.5665, lng: 126.978, address: 'Apgujeong-dong, Gangnam-gu, Seoul'}
  ];

  React.useEffect(() => {
    if (!map) {
      const m = L.map(mapRef.current, {
        center: [48.8655, 2.3212],
        zoom: 2,
        scrollWheelZoom: false
      });

      // Custom styled tile layer (dark theme with gold highlights)
      L.tileLayer(
        'https://tiles.stadiamaps.com/tiles/alidade_dark/{z}/{x}/{y}{r}.png',
        {
          maxZoom: 18,
          attribution: '&copy; Stadia Maps'
        }
      ).addTo(m);

      // Add markers with popups
      locations.forEach(loc => {
        L.marker([loc.lat, loc.lng], {icon: goldIcon})
          .addTo(m)
          .bindPopup(`<b>${loc.name}</b><br>${loc.address}`);
      });

      setMap(m);
    }

    // Cleanup on unmount
    return () => {
      if (map) {
        map.remove();
      }
    };
  }, [map]);

  const containerStyle = {
    height: '80vh',
    margin: '40px auto',
    maxWidth: '800px',
    fontFamily: '"Times New Roman", serif',
    color: '#d4af37'
  };

  return React.createElement('div', {style: containerStyle},
    React.createElement('h2', {style: {textAlign: 'center', marginBottom: '20px'}}, 'Dior Store Locations'),
    React.createElement('div', {ref: mapRef, style: {height: '100%', borderRadius: '8px', boxShadow: '0 0 15px rgba(212, 175, 55, 0.7)'}})
  );
}

export default MapPage;
