import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function Map() {
  const containerStyle = {
    fontFamily: "'Playfair Display', serif",
    color: '#1a1a1a',
    backgroundColor: '#fff',
    minHeight: '80vh',
    padding: '40px 20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const headingStyle = {
    fontSize: '3rem',
    fontWeight: 300,
    marginBottom: '24px',
    textTransform: 'uppercase',
    letterSpacing: '0.15em',
  };

  const mapStyle = {
    height: '60vh',
    width: '100%',
    maxWidth: '900px',
    borderRadius: '12px',
    boxShadow: '0 8px 20px rgba(0,0,0,0.12)',
    border: '1px solid #ddd',
  };

  useEffect(() => {
    const map = L.map('map', {
      center: [48.8566, 2.3522],
      zoom: 5,
      zoomControl: false,
      scrollWheelZoom: false,
      dragging: true,
    });

    // Custom monochrome style tiles from Carto (good Dior-ish minimal vibe)
    L.tileLayer(
      'https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png',
      {
        attribution:
          '&copy; <a href="https://carto.com/attributions">CARTO</a> &copy; OpenStreetMap contributors',
        maxZoom: 18,
      }
    ).addTo(map);

    // Dior boutiques (fictional addresses)
    const stores = [
      { coords: [48.866667, 2.333333], address: 'Paris Store, 15 Rue de la Paix, Paris' },
      { coords: [43.296482, 5.36978], address: 'Marseille Store, 12 Quai des Belges, Marseille' },
      { coords: [45.764043, 4.835659], address: 'Lyon Store, 20 Rue de la République, Lyon' },
      { coords: [50.62925, 3.057256], address: 'Lille Store, 33 Rue Faidherbe, Lille' },
      { coords: [43.604652, 1.444209], address: 'Toulouse Store, 11 Place du Capitole, Toulouse' },
      { coords: [44.837789, -0.57918], address: 'Bordeaux Store, 5 Cours de l’Intendance, Bordeaux' },
      { coords: [49.258329, 4.031696], address: 'Reims Store, 7 Place Drouet d’Erlon, Reims' },
      { coords: [47.218371, -1.553621], address: 'Nantes Store, 25 Rue Crébillon, Nantes' },
      { coords: [45.188529, 5.724524], address: 'Grenoble Store, 10 Rue Lafayette, Grenoble' },
      { coords: [43.710173, 7.261953], address: 'Nice Store, 18 Avenue Jean Médecin, Nice' },
    ];

    stores.forEach(({ coords, address }) => {
      L.marker(coords)
        .addTo(map)
        .bindPopup(`<b>Dior Boutique</b><br>${address}`);
    });

    return () => map.remove();
  }, []);

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h2', { style: headingStyle }, 'Our Boutiques'),
    React.createElement('div', { id: 'map', style: mapStyle })
  );
}

export default Map;
