import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function NewProduct() {
  const [formData, setFormData] = useState({ name: '', size: '', model: '' });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  function validate() {
    const errs = {};
    if (!formData.name.trim()) errs.name = 'Name is required';
    if (!formData.size.trim()) errs.size = 'Size is required';
    if (!formData.model.trim()) errs.model = 'Model is required';
    setErrors(errs);
    return Object.keys(errs).length === 0;
  }

  function handleChange(e) {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!validate()) return;

    // Create a unique URL for the product (could be your domain + query params)
    // Example: http://yourdomain.com/product?name=skirt&size=34&model=mini
    const baseUrl = window.location.origin;
    const productUrl = `${baseUrl}/product?name=${encodeURIComponent(formData.name)}&size=${encodeURIComponent(formData.size)}&model=${encodeURIComponent(formData.model)}`;

    // Redirect to QRGenerate page with productUrl as a query param
    navigate(`/qr-generate?link=${encodeURIComponent(productUrl)}`);
  }

  const containerStyle = { /* your existing styles */ };
  const labelStyle = { /* your existing styles */ };
  const inputStyle = { /* your existing styles */ };
  const buttonStyle = { /* your existing styles */ };
  const errorStyle = { /* your existing styles */ };

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h2', null, 'Add New Product'),
    React.createElement(
      'form',
      { onSubmit: handleSubmit, noValidate: true },
      React.createElement('label', { htmlFor: 'name', style: labelStyle }, 'Name'),
      errors.name && React.createElement('div', { style: errorStyle }, errors.name),
      React.createElement('input', {
        style: inputStyle,
        type: 'text',
        id: 'name',
        name: 'name',
        value: formData.name,
        onChange: handleChange,
      }),

      React.createElement('label', { htmlFor: 'size', style: labelStyle }, 'Size'),
      errors.size && React.createElement('div', { style: errorStyle }, errors.size),
      React.createElement('input', {
        style: inputStyle,
        type: 'text',
        id: 'size',
        name: 'size',
        value: formData.size,
        onChange: handleChange,
      }),

      React.createElement('label', { htmlFor: 'model', style: labelStyle }, 'Model'),
      errors.model && React.createElement('div', { style: errorStyle }, errors.model),
      React.createElement('input', {
        style: inputStyle,
        type: 'text',
        id: 'model',
        name: 'model',
        value: formData.model,
        onChange: handleChange,
      }),

      React.createElement(
        'button',
        { type: 'submit', style: buttonStyle },
        'Generate QR Code'
      )
    )
  );
}

export default NewProduct;
