const containerStyle = {
  fontFamily: "'Playfair Display', serif",
  color: '#1a1a1a',
  backgroundColor: '#fff',
  minHeight: '80vh',
  padding: '60px 20px',
  display: 'flex',
  flexDirection: 'column',
  maxWidth: '480px',
  margin: '0 auto',
};

const headingStyle = {
  fontSize: '3rem',
  fontWeight: 300,
  marginBottom: '40px',
  textTransform: 'uppercase',
  letterSpacing: '0.1em',
  textAlign: 'center',
};

const labelStyle = {
  fontWeight: 400,
  marginBottom: '8px',
  fontSize: '1rem',
};

const inputStyle = {
  width: '100%',             // Make input take full container width
  padding: '14px 12px',     // More padding for comfortable input
  fontSize: '1.1rem',       // Slightly bigger font
  marginBottom: '24px',
  border: '1px solid #aaa',
  borderRadius: '4px',
  fontFamily: "'Playfair Display', serif",
  boxSizing: 'border-box',  // Make padding included in width
};

const buttonStyle = {
  backgroundColor: '#000',
  color: '#fff',
  border: 'none',
  padding: '16px',
  fontSize: '1.1rem',
  cursor: 'pointer',
  textTransform: 'uppercase',
  letterSpacing: '0.1em',
  borderRadius: '2px',
  fontWeight: 400,
  width: '100%',            // Button full width for balanced look
};

const errorStyle = {
  color: 'red',
  marginBottom: '16px',
  fontSize: '0.9rem',
};
