import React, { useState } from 'react';
import QRCode from 'qrcode.react';

function QRGenerate() {
  const containerStyle = {
    fontFamily: "'Playfair Display', serif",
    color: '#1a1a1a',
    backgroundColor: '#fff',
    minHeight: '80vh',
    padding: '60px 20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    textAlign: 'center',
  };

  const headingStyle = {
    fontSize: '3rem',
    fontWeight: 300,
    marginBottom: '40px',
    textTransform: 'uppercase',
    letterSpacing: '0.1em',
  };

  const inputStyle = {
    padding: '12px',
    fontSize: '1rem',
    marginBottom: '24px',
    border: '1px solid #aaa',
    borderRadius: '4px',
    width: '280px',
    fontFamily: "'Playfair Display', serif",
  };

  const qrWrapper = {
    marginTop: '40px',
  };

  const [url, setUrl] = useState('');
  const [inputVal, setInputVal] = useState('');

  function handleChange(e) {
    setInputVal(e.target.value);
  }

  function generate() {
    setUrl(inputVal.trim());
  }

  return React.createElement(
    'div',
    { style: containerStyle },
    React.createElement('h2', { style: headingStyle }, 'Generate QR Code'),
    React.createElement('input', {
      style: inputStyle,
      type: 'text',
      placeholder: 'Enter product URL',
      value: inputVal,
      onChange: handleChange,
    }),
    React.createElement(
      'button',
      {
        style: { ...inputStyle, cursor: 'pointer', width: '280px', backgroundColor: '#000', color: '#fff', border: 'none', textTransform: 'uppercase', letterSpacing: '0.1em' },
        onClick: generate,
      },
      'Generate QR'
    ),
    url &&
      React.createElement(
        'div',
        { style: qrWrapper },
        React.createElement(QRCode, { value: url, size: 180 })
      )
  );
}

export default QRGenerate;
