import React from 'react';
import {useNavigate} from 'react-router-dom';

function NewProduct() {
  const [form, setForm] = React.useState({name: '', size: '', model: ''});
  const [errors, setErrors] = React.useState({});
  const navigate = useNavigate();

  const style = {
    maxWidth: '400px',
    margin: '40px auto',
    fontFamily: '"Times New Roman", serif',
    color: '#000',
    backgroundColor: '#fff',
    padding: '20px',
    border: '1px solid #d4af37',
    borderRadius: '8px'
  };

  const labelStyle = {display: 'block', marginBottom: '8px', fontWeight: 'bold'};
  const inputStyle = {
    width: '100%',
    padding: '8px',
    marginBottom: '12px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '1rem'
  };

  const errorStyle = {color: 'red', marginBottom: '12px'};
  const buttonStyle = {
    backgroundColor: '#d4af37',
    border: 'none',
    padding: '10px 15px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '1rem'
  };

  function validate() {
    const newErrors = {};
    if (!form.name.trim()) newErrors.name = 'Name is required';
    if (!form.size.trim()) newErrors.size = 'Size is required';
    if (!form.model.trim()) newErrors.model = 'Model is required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }

  function handleChange(e) {
    setForm({...form, [e.target.name]: e.target.value});
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (validate()) {
      const productId = Date.now() + '-' + Math.random().toString(36).substring(2, 7);
      const productLink = window.location.origin + '/product/' + productId;
      const products = JSON.parse(localStorage.getItem('products') || '{}');
      products[productId] = form;
      localStorage.setItem('products', JSON.stringify(products));

      navigate('/qr-generate?link=' + encodeURIComponent(productLink));
    }
  }

  return React.createElement('form', {style: style, onSubmit: handleSubmit, noValidate: true},
    React.createElement('label', {style: labelStyle, htmlFor: 'name'}, 'Name'),
    React.createElement('input', {
      id: 'name', name: 'name', type: 'text',
      value: form.name, onChange: handleChange,
      style: inputStyle, 'aria-describedby': 'nameError'
    }),
    errors.name && React.createElement('div', {id: 'nameError', style: errorStyle}, errors.name),

    React.createElement('label', {style: labelStyle, htmlFor: 'size'}, 'Size'),
    React.createElement('input', {
      id: 'size', name: 'size', type: 'text',
      value: form.size, onChange: handleChange,
      style: inputStyle, 'aria-describedby': 'sizeError'
    }),
    errors.size && React.createElement('div', {id: 'sizeError', style: errorStyle}, errors.size),

    React.createElement('label', {style: labelStyle, htmlFor: 'model'}, 'Model'),
    React.createElement('input', {
      id: 'model', name: 'model', type: 'text',
      value: form.model, onChange: handleChange,
      style: inputStyle, 'aria-describedby': 'modelError'
    }),
    errors.model && React.createElement('div', {id: 'modelError', style: errorStyle}, errors.model),

    React.createElement('button', {type: 'submit', style: buttonStyle}, 'Create Product')
  );
}

export default NewProduct;
