import React from 'react';

function Home() {
  const containerStyle = {
    fontFamily: "'Playfair Display', serif",
    color: '#1a1a1a',
    backgroundColor: '#fff',
    minHeight: '80vh',
    padding: '80px 20px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  };

  const headingStyle = {
    fontSize: '5rem',
    fontWeight: 300,
    letterSpacing: '0.15em',
    margin: 0,
    textTransform: 'uppercase',
    lineHeight: 1.1,
  };

  const lineStyle = {
    width: '60px',
    height: '2px',
    backgroundColor: '#000',
    margin: '32px 0',
    opacity: 0.3,
  };

  const pStyle = {
    fontSize: '1.25rem',
    maxWidth: '460px',
    fontWeight: 300,
    letterSpacing: '0.05em',
    lineHeight: 1.6,
    color: '#444',
    margin: 0,
  };

  const galleryStyle = {
    marginTop: '50px',
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '30px',
    width: '100%',
    maxWidth: '1000px',
  };

  const imgStyle = {
    width: '100%',
    height: '350px',
    objectFit: 'cover',
    borderRadius: '10px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    cursor: 'pointer',
  };

  const handleMouseEnter = (e) => {
    e.currentTarget.style.transform = 'scale(1.05)';
    e.currentTarget.style.boxShadow = '0 10px 20px rgba(0,0,0,0.2)';
  };

  const handleMouseLeave = (e) => {
    e.currentTarget.style.transform = 'scale(1)';
    e.currentTarget.style.boxShadow = '0 4px 12px rgba(0,0,0,0.1)';
  };

  const images = [
    'https://cdn.dior.com/couture/ecommerce/media/catalog/product/cache/5/image/1200x1800/9df78eab33525d08d6e5fb8d27136e95/b/l/bll645fble_h144_013_dior_oblique.jpg',
    'https://cdn.dior.com/couture/ecommerce/media/catalog/product/cache/5/image/1200x1800/9df78eab33525d08d6e5fb8d27136e95/c/h/christian-dior-diorbook-tote-bag-3dq683ymc-m927_d765a.jpg',
    'https://cdn.dior.com/couture/ecommerce/media/catalog/product/cache/5/image/1200x1800/9df78eab33525d08d6e5fb8d27136e95/b/l/bll645fble_h144_019_dior_oblique.jpg',
    'https://cdn.dior.com/couture/ecommerce/media/catalog/product/cache/5/image/1200x1800/9df78eab33525d08d6e5fb8d27136e95/a/v/avd341ymc_s900_d017_090_dior_oblique.jpg',
    'https://cdn.dior.com/couture/ecommerce/media/catalog/product/cache/5/image/1200x1800/9df78eab33525d08d6e5fb8d27136e95/m/a/may645fble_h070_006_dior_oblique.jpg',
    'https://cdn.dior.com/couture/ecommerce/media/catalog/product/cache/5/image/1200x1800/9df78eab33525d08d6e5fb8d27136e95/e/d/edg881fble_h010_999_dior_oblique.jpg',
  ];

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>DIOR</h1>
      <div style={lineStyle} />
      <p style={pStyle}>
        Experience the elegance of Diorâ€™s iconic products and discover our exclusive boutiques worldwide.
      </p>

      <div style={galleryStyle}>
        {images.map((src, idx) => (
          <img
            key={idx}
            src={src}
            alt={`Dior clothing ${idx + 1}`}
            style={imgStyle}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          />
        ))}
      </div>
    </div>
  );
}

export default Home;
